function getMouse(e){return[Math.round(e.clientX/window.innerWidth*70),Math.round(e.clientY/window.innerHeight*60)]}const modelViewer=document.querySelector("#myModel"),modelMobileViewer=document.querySelector("#myMobileModel");document.onmousemove=function(e){const n=getMouse(e);var o=n[0],t=n[1];modelViewer.cameraOrbit=`${-1290+o}deg`+`${120-t}deg `+'"700m"',modelMobileViewer.cameraOrbit=`${-1290+o}deg`+`${120-t}deg `+'"700m"',document.getElementById("coordinates").innerHTML="x:/"+32*n[0]+", y:/"+32*n[1]},window.onload=function(){var e=document.getElementById("announcement-overlay");setTimeout(function(){e.style.right="30px"},3e3)};var closer=document.getElementById("announcement-closer"),modal=document.getElementById("announcement-overlay");closer.addEventListener("click",function(){modal.style.right="-100%"});